import { validateHorizontalPosition } from '@angular/cdk/overlay';
import { Component, Inject, OnInit } from '@angular/core';
import { AbstractControl, AsyncValidatorFn, FormBuilder, FormGroup, Validators } from '@angular/forms';
import { MatDialogRef, MAT_DIALOG_DATA } from '@angular/material/dialog';
import { MatSnackBar } from '@angular/material/snack-bar';
import { combineAll, map, tap } from 'rxjs/operators';
import { Shipper } from 'src/app/models/shipperView';
import { ApishippersService } from 'src/app/services/apishippers.service';

@Component({
  selector: 'app-dialogShipper',
  templateUrl: './dialogShipper.component.html',
  styleUrls: ['./dialogShipper.component.scss']
})
export class DialogShipperComponent implements OnInit {
  val = Validators.maxLength(25);
  frm: FormGroup = this.fb.group({
    company: ['', [Validators.required, this.val], [nameCheck(this.apiShipper)]],
    phone: ['', [Validators.required, Validators.maxLength(25)]],
  })

  constructor(public dialogREF : MatDialogRef<DialogShipperComponent>,
              public apiShipper: ApishippersService,
              private snackBar : MatSnackBar,
              private readonly fb : FormBuilder,
              @Inject(MAT_DIALOG_DATA) public shipperD : Shipper) { 
                if(this.shipperD != null){
                  const companyD = this.frm.get('company');
                  companyD?.setValue(this.shipperD.CompanyName);
                  const phoneD = this.frm.get('phone');
                  phoneD?.setValue(this.shipperD.PhoneNumber)                 
                }
  }

  ngOnInit(): void {
    if(this.shipperD != null){
      const companyEd = this.frm.get('company');
      companyEd?.removeAsyncValidators(nameCheck(this.apiShipper));
      companyEd?.removeValidators(this.val);
      companyEd?.updateValueAndValidity();
      //companyEd?.removeValidators(xxxxx); agregar validation async a borrar, y fijarte si usar constante afuera para ambas
    }
  }
  close(){
    this.dialogREF.close();
  }
  clean(){
    const companyDom = this.frm.get('company');
    const phoneDom = this.frm.get('phone');
    if(companyDom || phoneDom){
        companyDom?.setValue('');
        phoneDom?.setValue('');
      }


  }
  addShipper(){
    const shipper : Shipper = {CompanyName: this.frm.get('company')?.value, PhoneNumber: this.frm.get('phone')?.value, ShipperID: 0 };
    this.apiShipper.InsertUpdateShipper(shipper).subscribe(response => {
      if(response.sucess == 1){
        this.close();
        this.snackBar.open(response.message, '', {duration:2000});
      }
      else{
        alert("Error!!! while adding the Shipper");
      }
    })
  }
  editShipper(){
    const shipper : Shipper = {CompanyName: this.frm.get('company')?.value, PhoneNumber: this.frm.get('phone')?.value, ShipperID: this.shipperD.ShipperID};
    this.apiShipper.InsertUpdateShipper(shipper).subscribe(Response => {
      if(Response.sucess == 1){
        this.close();
        this.snackBar.open(Response.message, '', {duration:3500});
      }
      else{
        alert("Error!!! while updating the Shipper")
      }
    })
  }


}
export function nameCheck(apiShipper: any): AsyncValidatorFn {
  return (control : AbstractControl) => {
  const shipper : Shipper = {CompanyName: control.value, PhoneNumber: "", ShipperID: 0};
  return apiShipper.validationName(shipper)
  .pipe(
    map(({sucess}) => (sucess == 1) ? {companyExists: true} : null)
  );
};
}
<div class="container">
         
        <h1 mat-dialog-title>{{shipperD!=null ? 'Edit Shipper' : 'New Shipper'}}</h1>
        <div mat-dialog-content >
            <form [formGroup]="frm" novalidate>
            <mat-form-field>
                <mat-label>Company Name</mat-label>
                <input type="text" matInput formControlName = "company" >                                                
            </mat-form-field>
            <div *ngIf="frm.get('company')?.hasError('required') && (frm.get('company')?.dirty || frm.get('company')?.touched)" class="sideUp">You must provide a <strong>Company Name</strong></div> 
            <div *ngIf="frm.get('company')?.hasError('maxlength')" class="sideUp">Only <strong> 25 Characthers maximum</strong></div> 
            <div *ngIf="frm.get('company')?.hasError('companyExists') && frm.get('company')?.dirty " class="sideUp"><strong>that name exists, try another</strong></div> 

            <mat-form-field>
                <mat-label>Phone Number</mat-label>
                <input type="text" matInput formControlName = "phone">               
            </mat-form-field>
            <div *ngIf="frm.get('phone')?.hasError('required') && frm.get('phone')?.dirty" class="sideUp">You must provide a <strong>Phone Number</strong></div> 
            <div *ngIf="frm.get('phone')?.hasError('maxlength')" class="sideUp">Only <strong> 25 Characthers maximum</strong></div> 

            <div mat-dialog-actions style="justify-content: center;margin-bottom: 30px;">
                <button mat-raised-button color="primary" type="submit" [disabled]="this.frm.invalid" (click)="shipperD !== null ? editShipper() : addShipper()"> Save </button>
                <button mat-raised-button color="warn" type="button" (click)="close()">Close</button>
                <button mat-raised-button color="accent" type="button" (click)="clean()">Clean Up</button>
            </div>       
        </form>
        </div>      
    
</div>
